using ShareScreen.Core.Interfaces.Core;

namespace ShareScreen.Core.Core.Payload
{
    public class SSPayload<T> : ISSPayload
    {
        private T value;

        public SSPayload()
        {
        }

        public SSPayload(T val)
        {
            Value = val;
        }
        public T Value
        {
            get
            {
                return this.value;
            }
            set
            {
                this.value = value;
            }
        }
    }//end SSPayload
}//end namespace Payload